con:configuration.scriptText:
import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent
def holder = new JsonSlurper().parseText(response)
def username=holder.data.username
log.info username
assert messageExchange.responseHeaders["#status#"] == ["HTTP/1.1 200 OK"]

if(username == "upskills_admin")
{
	log.info "success"
}
else
{log.info "Not  valid"}

